const CryptoJS = require('crypto-js');

var myString = "https://www.titanesmedellin.com/";
var myPassword = "myPassword";

var encrypted = CryptoJS.AES.encrypt(myString, myPassword).toString();
console.log(encrypted);

var decrypted = CryptoJS.AES.decrypt(encrypted, myPassword).toString(CryptoJS.enc.Utf8);
console.log(decrypted);