const cryptoJS = require('crypto-js');

var key = encText('b/Gu5posvwDsXjfirtaq4g==');
var iv = encText('5D9r9ZVzEYYgha93/aUK2w==');

function dec(string){
        return cryptoJS.enc.Utf8.stringify(
            cryptoJS.AES.decrypt({ciphertext: encText(string)},
                key, { iv: iv })
        );
}

function enc(data){return cryptoJS.AES.encrypt(data, key, { iv: iv});}

function encText(text) { return cryptoJS.enc.Base64.parse(text) };


    
